    typeof fn === 'function'
      && typeof a1.map === 'function'
      && typeof a2.ap === 'function'
      && typeof a3.ap === 'function',
    typeMismatch('Applicative f => (a -> b -> c -> d) -> f a -> f b -> f c -> f d', [getType(fn), getType(a1), getType(a2)].join(' -> '), 'liftA2'),
